&lt;/span&gt;&lt;/span&gt;&lt;span style=&#34;display:flex;&#34;&gt;&lt;span&gt;    cbErrorSchema: &lt;span style=&#34;color:#ff7b72&#34;&gt;z.null&lt;/span&gt;(),
&lt;/span&gt;&lt;/span&gt;&lt;span style=&#34;display:flex;&#34;&gt;&lt;span&gt;    responseSchema: &lt;span style=&#34;color:#ff7b72&#34;&gt;z.object&lt;/span&gt;({ token: &lt;span style=&#34;color:#ff7b72&#34;&gt;z.string&lt;/span&gt;() }),
